import React, { useEffect } from "react";
import styles from "./About.module.css";
import aboutImage from "../../assets/images/logo2.png";
import { Code, Users, Lightbulb } from "lucide-react";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

const featureData = [
  {
    icon: <Code size={24} color="#ffffff" />,
    title: "Hands-On Learning",
    text: "We believe in learning by doing—exploring real-world projects that sharpen our skills and creativity.",
  },
  {
    icon: <Users size={24} color="#ffffff" />,
    title: "Team Collaboration",
    text: "Working together as a team, we exchange ideas and grow through shared knowledge and experience.",
  },
  {
    icon: <Lightbulb size={24} color="#ffffff" />,
    title: "Early Innovation",
    text: "Even as we begin, we're experimenting with simple but creative solutions that reflect our drive to make an impact.",
  },
];

const About = () => {
  const controls = useAnimation();
  const { ref, inView } = useInView({ threshold: 0.2 });

  useEffect(() => {
    if (inView) {
      controls.start({
        opacity: 1,
        y: 0,
        transition: { type: "spring", stiffness: 60, damping: 15 },
      });
    } else {
      controls.start({ opacity: 0, y: 60 });
    }
  }, [inView, controls]);

  return (
    <section id="about" className={styles.about} ref={ref} aria-label="About NetScorp">
      <motion.div
        className={styles.container}
        initial={{ opacity: 0, y: 60 }}
        animate={controls}
      >
        <h1 className={styles.sectionHeading}>
          About Us
          <span className={styles.underline}></span>
        </h1>

        <div className={styles.image}>
          <img src={aboutImage} alt="NetScorp team logo" className={styles.photo} role="img" />
        </div>

        <div className={styles.content}>
          <h2 className={styles.title}>Laying the Foundation for Digital Innovation</h2>
          <p className={styles.description}>
            At NetScorp, we're a team of aspiring developers taking our first steps into the exciting world of web development. We're passionate about learning, building, and growing—one line of code at a time.
          </p>

          {featureData.map((feature, index) => (
            <motion.article
              key={index}
              className={styles.featureBox}
              initial={{ opacity: 0, y: 40 }}
              animate={controls}
              transition={{ delay: index * 0.2 }}
            >
              <div className={styles.icon}>{feature.icon}</div>
              <div>
                <h3 className={styles.subheading}>{feature.title}</h3>
                <p className={styles.subtext}>{feature.text}</p>
              </div>
            </motion.article>
          ))}

          <p className={styles.finalText}>
            NetScorp is more than just a name—it's a mindset. As we continue our journey, we're focused on building meaningful web experiences, nurturing our skills, and embracing every opportunity to grow in the tech world.
          </p>
        </div>
      </motion.div>
    </section>
  );
};

export default About;
