import React from "react";
import styles from "./Hero.module.css";
import { motion } from "framer-motion";
import men from "../../assets/images/logo1.png";

const textVariants = {
  hidden: { opacity: 0, y: 20, letterSpacing: "0.1em" },
  visible: {
    opacity: 1,
    y: 0,
    letterSpacing: "0.3em",
    transition: {
      duration: 1,
      ease: "easeOut",
      staggerChildren: 0.05,
    },
  },
};

const letterVariant = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0, transition: { type: "spring", stiffness: 300 } },
};

const Hero = () => {
  // split heading into letters for stagger animation
  const heading = "Welcome to Syntax.net";
  const paragraph = "Your one-stop destination for amazing web experiences.";

  return (
    <section id="hero" className={styles.hero}>
      <div className={styles.container}>
        <motion.div
          className={styles.text}
          variants={textVariants}
          initial="hidden"
          animate="visible"
        >
          <h1 className={styles.heading}>
            {heading.split("").map((char, index) => (
              <motion.span key={index} variants={letterVariant} className={styles.letter}>
                {char === " " ? "\u00A0" : char}
              </motion.span>
            ))}
          </h1>
          <motion.p
            className={styles.subheading}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.2, duration: 1, ease: "easeOut" }}
          >
            {paragraph}
          </motion.p>
          <motion.a
            href="#projects"
            className={styles.cta}
            whileHover={{ scale: 1.1, backgroundColor: "#ff4757" }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            See Projects
          </motion.a>
        </motion.div>

        <motion.div
          className={styles.image}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src={men} alt="Two men coding" className={styles.logo} />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
