// src/sections/Hero/Hero.jsx

import React from "react";
import styles from "./Hero.module.css";
import { motion } from "framer-motion";
import men from "../../assets/images/logo1.png";

const Hero = () => {
  return (
    <section id="hero" className={styles.hero}>
      <div className={styles.container}>
        <motion.div
          className={styles.text}
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <h1>Welcome to Syntax.net</h1>
          <p>Your one-stop destination for amazing web experiences.</p>
          <a href="#projects" className={styles.cta}>
            See Projects
          </a>
        </motion.div>

        <motion.div
          className={styles.image}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src={men} alt="Two men coding" className={styles.logo} />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
