import React, { useState, useEffect } from "react";
import styles from "./Hero.module.css";
import { motion } from "framer-motion";
import men from "../../assets/images/logo1.png";
import gif from "../../assets/images/258.gif"; // Import your GIF here

const Hero = () => {
  const fullText = "Welcome to SyntaxNet";
  const [displayedText, setDisplayedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  useEffect(() => {
    let timer;

    const handleTyping = () => {
      const fullTxt = fullText;

      if (!isDeleting) {
        setDisplayedText(fullTxt.substring(0, displayedText.length + 1));
        setTypingSpeed(150);
        if (displayedText === fullTxt) {
          timer = setTimeout(() => setIsDeleting(true), 1000);
          return;
        }
      } else {
        setDisplayedText(fullTxt.substring(0, displayedText.length - 1));
        setTypingSpeed(75);
        if (displayedText === "") {
          setIsDeleting(false);
          setLoopNum(loopNum + 1);
        }
      }

      timer = setTimeout(handleTyping, typingSpeed);
    };

    timer = setTimeout(handleTyping, typingSpeed);

    return () => clearTimeout(timer);
  }, [displayedText, isDeleting, loopNum, typingSpeed]);

  return (
    <section id="hero" className={styles.hero}>
      <div className={styles.container}>
        <motion.div
          className={styles.text}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <h1 className={styles.typingHeading}>
            {displayedText}
            <span className={styles.cursor}>|</span>
          </h1>

          <motion.p
            className={styles.subheading}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.5, duration: 1, ease: "easeOut" }}
          >
            Your one-stop destination for amazing web experiences.<br />
            We specialize in building responsive, scalable, and user-focused websites.<br />
            Driven by innovation, powered by precision.<br />
            Let’s shape the future of the web—together.
          </motion.p>

          <motion.a
            href="#projects"
            className={styles.cta}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            See Projects
          </motion.a>
        </motion.div>

        <motion.div
          className={styles.image}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src={men} alt="Developer character" className={styles.logo} />
          <img src={gif} alt="Animated Graphic" className={styles.gif} />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
