// src/sections/About/About.jsx
import React from "react";
import styles from "./About.module.css";
import FadeIn from "../../components/FadeIn";
import aboutImage from "../../assets/images/logo2.png"; // Replace with your actual image

const About = () => {
  return (
    <section id="about" className={styles.about}>
      <FadeIn>
        <div className={styles.container}>
          {/* Left Side - Image */}
          <div className={styles.image}>
            <img
              src={aboutImage}
              alt="Anto Joseph"
              className={styles.photo}
            />
          </div>

          {/* Right Side - Text Content */}
          <div className={styles.textContent}>
            <h2 className={styles.heading}>About Me</h2>
            <p className={styles.text}>
              Hi, I’m Anto Joseph, a passionate web developer specializing in React, Node.js, HTML, CSS, and MongoDB. I’m currently pursuing my MCA at Amal Jyothi College and have completed my BCA from MES College Nedumkandam.
            </p>
            <p className={styles.text}>
              I love crafting beautiful and performant web applications with clean code and a focus on user-friendly experiences.
            </p>
          </div>
        </div>
      </FadeIn>
    </section>
  );
};

export default About;
