import React, { useState, useEffect } from "react";
import styles from "./Hero.module.css";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";
import men from "../../assets/images/logo1.png";

const Hero = () => {
  const fullTextArray = ["Welcome to", "NetScorp"];
  const [lineIndex, setLineIndex] = useState(0);
  const [displayedText, setDisplayedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  const controls = useAnimation();
  const { ref, inView } = useInView({ triggerOnce: false, threshold: 0.2 });

  useEffect(() => {
    if (inView) {
      controls.start({
        opacity: 1,
        y: 0,
        transition: { duration: 0.8, ease: "easeOut" },
      });
    } else {
      controls.start({ opacity: 0, y: 40 });
    }
  }, [inView]);

  useEffect(() => {
    let timer;

    const handleTyping = () => {
      const fullTxt = fullTextArray[lineIndex];

      if (!isDeleting) {
        setDisplayedText(fullTxt.substring(0, displayedText.length + 1));
        setTypingSpeed(150);

        if (displayedText === fullTxt) {
          if (lineIndex < fullTextArray.length - 1) {
            timer = setTimeout(() => {
              setLineIndex(lineIndex + 1);
              setDisplayedText("");
            }, 500);
            return;
          } else {
            timer = setTimeout(() => setIsDeleting(true), 1000);
            return;
          }
        }
      } else {
        setDisplayedText(fullTxt.substring(0, displayedText.length - 1));
        setTypingSpeed(75);

        if (displayedText === "") {
          if (lineIndex === 0) {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
          } else {
            setLineIndex(lineIndex - 1);
          }
        }
      }

      timer = setTimeout(handleTyping, typingSpeed);
    };

    timer = setTimeout(handleTyping, typingSpeed);

    return () => clearTimeout(timer);
  }, [displayedText, isDeleting, lineIndex, loopNum, typingSpeed]);

  return (
    <section id="hero" className={styles.hero} ref={ref}>
      <motion.div
        className={styles.container}
        initial={{ opacity: 0, y: 40 }}
        animate={controls}
      >
        <motion.div className={styles.text}>
          <h1 className={styles.typingHeading}>
            {lineIndex > 0 ? (
              <>
                {fullTextArray[0]}<br />
                {displayedText}
              </>
            ) : (
              <>
                {displayedText}
              </>
            )}
            <span className={styles.cursor}>|</span>
          </h1>

          <motion.p
            className={styles.subheading}
            initial={{ opacity: 0, y: 20 }}
            animate={controls}
            transition={{ delay: 0.5, duration: 1 }}
          >
            Your one-stop destination for amazing web experiences.<br />
            We specialize in building responsive, scalable, and user-focused websites.<br />
            Driven by innovation, powered by precision.<br />
            Let’s shape the future of the web—together.
          </motion.p>

          <motion.a
            href="#projects"
            className={styles.cta}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            See Projects
          </motion.a>
        </motion.div>

        <motion.div
          className={styles.image}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src={men} alt="Two men coding" className={styles.logo} />
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Hero;
