import React, { useState, useEffect } from "react";
import styles from "./Navbar.module.css";

const Navbar = () => {
  const [active, setActive] = useState(false);           // mobile menu toggle
  const [activeSection, setActiveSection] = useState("hero"); // scrollspy active section

  // Toggle mobile menu
  const handleToggle = () => setActive(!active);

  // Close mobile menu on link click
  const handleLinkClick = () => setActive(false);

  useEffect(() => {
    const navItems = ["hero", "about", "services", "projects", "contact"];

    const handleScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight / 2;

      for (let i = navItems.length - 1; i >= 0; i--) {
        const section = document.getElementById(navItems[i]);
        if (section) {
          const offsetTop = section.offsetTop;
          if (scrollPos >= offsetTop) {
            setActiveSection(navItems[i]);
            break;
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll(); // initial check on mount

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <nav className={styles.navbar}>
      <div className={styles.logo}>Syntax.net</div>

      <ul className={`${styles.navLinks} ${active ? styles.active : ""}`}>
        <li>
          <a
            href="#hero"
            onClick={handleLinkClick}
            className={activeSection === "hero" ? styles.activeLink : ""}
          >
            Home
          </a>
        </li>
        <li>
          <a
            href="#about"
            onClick={handleLinkClick}
            className={activeSection === "about" ? styles.activeLink : ""}
          >
            About
          </a>
        </li>
        <li>
          <a
            href="#services"
            onClick={handleLinkClick}
            className={activeSection === "services" ? styles.activeLink : ""}
          >
            Services
          </a>
        </li>
        <li>
          <a
            href="#projects"
            onClick={handleLinkClick}
            className={activeSection === "projects" ? styles.activeLink : ""}
          >
            Projects
          </a>
        </li>
        <li>
          <a
            href="#contact"
            onClick={handleLinkClick}
            className={activeSection === "contact" ? styles.activeLink : ""}
          >
            Contact
          </a>
        </li>
      </ul>

      <div
        className={`${styles.hamburger} ${active ? styles.active : ""}`}
        onClick={handleToggle}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>
    </nav>
  );
};

export default Navbar;
