import React, { useState, useEffect } from 'react';
import styles from './Navbar.module.css';
import logo from '../../assets/images/net.jpg';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const navbarClasses = `${styles.navbar} ${scrolled ? styles.scrolled : ''}`;

  return (
    <nav className={navbarClasses}>
      <img src={logo} alt="NetScorp Logo" className={styles.logo} />
      <ul className={`${styles.navLinks} ${isOpen ? styles.active : ''}`}>
        <li><a href="#home" className={styles.activeLink}>Home</a></li>
        <li><a href="#about">About</a></li>
        <li><a href="#services">Services</a></li>
        <li><a href="#projects">Projects</a></li>
        <li><a href="#contact">Contact</a></li>
      </ul>
      <div
        className={`${styles.hamburger} ${isOpen ? styles.active : ''}`}
        onClick={() => setIsOpen(!isOpen)}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>
    </nav>
  );
};

export default Navbar;
