import React, { useEffect, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';
import styles from './Hero.module.css';
import logo from '../assets/netscorp-logo.png'; // Adjust the path as needed

const Hero = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = 'Welcome to NetScorp';
  const controls = useAnimation();

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setDisplayedText((prev) => prev + fullText.charAt(index));
      index++;
      if (index === fullText.length) clearInterval(interval);
    }, 100);

    controls.start({ opacity: 1, y: 0 });
  }, [controls]);

  return (
    <section className={styles.hero}>
      <div className={styles.container}>
        <div className={styles.text}>
          <h1 className={styles.typingHeading}>
            {displayedText}
            <span className={styles.cursor}>|</span>
          </h1>

          <motion.h2
            className={styles.subTitle}
            initial={{ opacity: 0, y: 20 }}
            animate={controls}
            transition={{ delay: 0.6, duration: 1 }}
          >
            Tech Labs
          </motion.h2>

          <p className={styles.subheading}>
            Your one-stop destination for amazing web experiences.
            We specialize in building responsive, scalable, and user-focused websites.
            Driven by innovation, powered by precision.
            Let’s shape the future of the web—together.
          </p>

          <a href="#projects" className={styles.cta}>See Projects</a>
        </div>

        <div className={styles.image}>
          <img src={logo} alt="NetScorp Logo" className={styles.logo} />
        </div>
      </div>
    </section>
  );
};

export default Hero;
