import React, { useState, useEffect } from "react";
import styles from "./Hero.module.css";
import { motion } from "framer-motion";
import men from "../../assets/images/logo1.png";

const Hero = () => {
  const fullText = "Welcome to Syntax.net";
  const [displayedText, setDisplayedText] = useState("");

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setDisplayedText(fullText.slice(0, index + 1));
      index++;
      if (index === fullText.length) clearInterval(interval);
    }, 100); // typing speed in ms per character

    return () => clearInterval(interval);
  }, []);

  return (
    <section id="hero" className={styles.hero}>
      <div className={styles.container}>
        <motion.div
          className={styles.text}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <h1 className={styles.typingHeading}>{displayedText}<span className={styles.cursor}>|</span></h1>

          <motion.p
            className={styles.subheading}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: fullText.length * 0.1 + 0.5, duration: 1, ease: "easeOut" }}
          >
            Your one-stop destination for amazing web experiences.
          </motion.p>

          <motion.a
            href="#projects"
            className={styles.cta}
            whileHover={{ scale: 1.1, backgroundColor: "#ff4757" }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            See Projects
          </motion.a>
        </motion.div>

        <motion.div
          className={styles.image}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src={men} alt="Two men coding" className={styles.logo} />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
