import React from "react";
import styles from "./About.module.css";
import aboutImage from "../../assets/images/logo2.png";
import { Code, Users } from "lucide-react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";

const About = () => {
  const { ref, inView } = useInView({ triggerOnce: true, threshold: 0.2 });

  return (
    <section id="about" className={styles.about} ref={ref}>
      <motion.div
        className={styles.container}
        initial={{ opacity: 0, y: 40 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 1, ease: "easeOut" }}
      >
        {/* About Us Heading */}
        <h1 className={styles.sectionHeading}>
          About Us
          <span className={styles.underline}></span>
        </h1>

        {/* Left - Image */}
        <div className={styles.image}>
          <img src={aboutImage} alt="About Us" className={styles.photo} />
        </div>

        {/* Right - Content */}
        <div className={styles.content}>
          <h2 className={styles.title}>Innovative Web Solutions for a Digital Future</h2>
          <p className={styles.description}>
            At SyntaxNet, we develop cutting-edge web solutions that bridge the gap between technology and practical application.
          </p>

          <div className={styles.featureBox}>
            <div className={styles.icon}><Code size={24} color="#ffffff" /></div>
            <div>
              <h3 className={styles.subheading}>Innovative Development Practices</h3>
              <p className={styles.subtext}>
                Utilizing the latest technologies to create efficient solutions for our clients.
              </p>
            </div>
          </div>

          <div className={styles.featureBox}>
            <div className={styles.icon}><Users size={24} color="#ffffff" /></div>
            <div>
              <h3 className={styles.subheading}>Collaboration and Teamwork</h3>
              <p className={styles.subtext}>
                Fostering a culture of collaboration among our talented team and students to deliver exceptional results.
              </p>
            </div>
          </div>

          <p className={styles.finalText}>
            At SyntaxNet, we harness advanced technologies to deliver applications that enhance learning experiences, promote a work culture among students, and foster self-sustainability, ensuring that our work meets the demands of the digital age.
          </p>
        </div>
      </motion.div>
    </section>
  );
};

export default About;
